var Engine = Matter.Engine,
    Render = Matter.Render,
    Runner = Matter.Runner,
    Body = Matter.Body,
    Bodies = Matter.Bodies,
    Composite = Matter.Composite;
    Composites = Matter.Composites;
    Common = Matter.Common,
    Mouse = Matter.Mouse;
    MouseConstraint = Matter.MouseConstraint;
    Events = Matter.Events;
    Collision = Matter.Collision;
    World = Matter.World;

// create an engine
var engine = Engine.create();

// create a renderer
var render = Render.create({
  element: document.body,
  engine: engine,
  options: {
    wireframes: false,
  }
});

const LOCATION = (100, 100);
const COLUMNS = 40;
const ROWS = 40;
const COLUMN_GAP = 2;
const ROW_GAP = 2;
const CIRCLE_SIZE = 2;

function getConstraintsFromBody(compo, body) {
  var constraints = Composite.allConstraints(compo);
  return constraints.filter(c =>  c.bodyA == body || c.bodyB == body);

}

// create two boxes and a ground
var cloth = Composites.stack(LOCATION[0], LOCATION[1], COLUMNS, ROWS, COLUMN_GAP, ROW_GAP, function(x, y) {
  return Bodies.circle(x, y, CIRCLE_SIZE, {
    collisionFilter: {
      category: -1,
      mask: -1,
      group: -1,
    }
  });
});
cloth = Composites.mesh(cloth, COLUMNS, ROWS, false, {
  render: {
    anchor: false,
  }
});
var anchor = Bodies.circle(100, 50, 20, {isStatic: true});
var anchorConstraints = [];


for (let i=0; i<COLUMNS; i++) {
  cloth.bodies[i].isStatic = true;
  var constraint = Matter.Constraint.create({
    bodyA: cloth.bodies[cloth.bodies.length - 1 - i],
    length: 100,
    bodyB: anchor,
    render: {
      visible: true,
    }
  })
  anchorConstraints.push(constraint);
  
}

